import { Card, GameMessage, ShowCardsPrompt, StoreLike, State, StateUtils } from '../../game';
import { ChooseCardsPrompt } from '../../game/store/prompts/choose-cards-prompt';
import { Effect } from '../../game/store/effects/effect';
import { WAS_ATTACK_USED, SHUFFLE_DECK } from '../../game/store/prefabs/prefabs';

export class Kakuna extends PokemonCard {
  public stage: Stage = Stage.STAGE_1;
  public evolvesFrom: string = 'Weedle';
  public cardType: CardType = G;
  public hp: number = 80;
  public weakness = [{ type: R }];
  public retreat = [C, C];

  public attacks = [
    {
      name: 'Multiply',
      cost: [G],
      damage: 0,
      text: 'Search your deck for up to 3 Kakuna and put them onto your Bench. Then, shuffle your deck.'
    }
  ];

  public set: string = 'CIN';
  public setNumber: string = '2';
  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Kakuna';
  public fullName: string = 'Kakuna CIN';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Multiply
    // Ref: set-guardians-rising/politoed.ts (Roll Call - search deck for specific Pokemon onto bench)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);

      if (player.deck.cards.length === 0) {
        return state;
      }

      const openSlots = player.bench.filter(b => b.cards.length === 0).length;
      if (openSlots === 0) {
        return SHUFFLE_DECK(store, state, player);
      }

      const blocked: number[] = [];
      player.deck.cards.forEach((card, index) => {
        if (!(card instanceof PokemonCard) || card.name !== 'Kakuna') {
          blocked.push(index);
        }
      });

      return store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_PUT_ONTO_BENCH,
        player.deck,
        { superType: SuperType.POKEMON },
        { min: 0, max: Math.min(3, openSlots), allowCancel: false, blocked }
      ), (selected: Card[]) => {
        const cards = selected || [];

        if (cards.length > 0) {
          store.prompt(state, new ShowCardsPrompt(
            opponent.id,
            GameMessage.CARDS_SHOWED_BY_THE_OPPONENT,
            cards
          ), () => { });

          cards.forEach(card => {
            const emptySlot = player.bench.find(b => b.cards.length === 0);
            if (emptySlot) {
              player.deck.moveCardTo(card, emptySlot);
              emptySlot.pokemonPlayedTurn = state.turn;
            }
          });
        }

        return SHUFFLE_DECK(store, state, player);
      });
    }

    return state;
  }
}
