  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: TLC
    // Ref: set-twilight-masquerade/sinistcha-ex.ts (deck shuffling pattern)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);

      // Build blocked list for benched Pokemon without damage counters
      const blocked: CardTarget[] = [];
      let hasDamagedBench = false;

      opponent.bench.forEach((bench, index) => {
        if (bench.cards.length === 0 || bench.damage === 0) {
          blocked.push({ player: PlayerType.TOP_PLAYER, slot: SlotType.BENCH, index });
        } else {
          hasDamagedBench = true;
        }
      });

      if (!hasDamagedBench) {
        return state;
      }

      store.prompt(state, new ChoosePokemonPrompt(
        player.id,
        GameMessage.CHOOSE_POKEMON,
        PlayerType.TOP_PLAYER,
        [SlotType.BENCH],
        { min: 1, max: 1, allowCancel: false, blocked }
      ), selected => {
        if (selected && selected.length > 0) {
          const target = selected[0];

          // Move tools first
          const tools = target.tools.slice();
          tools.forEach(tool => {
            target.moveCardTo(tool, opponent.deck);
          });

          // Move all cards (Pokemon + energy)
          const cards = target.cards.slice();
          cards.forEach(card => {
            target.moveCardTo(card, opponent.deck);
          });

          // Clear any effects/damage/conditions on the slot
          target.damage = 0;
          target.specialConditions = [];
          target.marker.markers = [];

          SHUFFLE_DECK(store, state, opponent);
        }
      });
    }

    // Attack 2: Ocean Cyclone
    // Ref: set-burning-shadows/gyarados.ts (Splash Burn - bench damage)
    if (WAS_ATTACK_USED(effect, 1, this)) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);

      opponent.bench.forEach(benched => {
        if (benched.cards.length > 0) {
          const damage = new PutDamageEffect(effect, 10);
          damage.target = benched;
          store.reduceEffect(state, damage);
        }
      });
    }

    return state;
  }
