import { PokemonCard } from '../../game/store/card/pokemon-card';
import { Stage, CardType, CardTag, SuperType } from '../../game/store/card/card-types';
import { Card, CardTarget, ChooseCardsPrompt, ChoosePokemonPrompt, GameMessage, PlayerType, SlotType, StoreLike, State } from '../../game';
import { EnergyCard } from '../../game/store/card/energy-card';
import { Effect } from '../../game/store/effects/effect';
import { WAS_ATTACK_USED } from '../../game/store/prefabs/prefabs';

export class CarbinkBreak extends PokemonCard {
  public tags = [CardTag.BREAK];
  public stage: Stage = Stage.BREAK;
  public evolvesFrom: string = 'Carbink';
  public cardType: CardType = F;
  public hp: number = 110;
  public retreat = [];

  public attacks = [
    {
      name: 'Diamond Gift',
      cost: [F],
      damage: 20,
      text: 'Attach 2 Energy cards from your discard pile to 1 of your Fighting PokÃ©mon.'
    }
  ];

  public set: string = 'FCO';
  public setNumber: string = '51';
  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Carbink BREAK';
  public fullName: string = 'Carbink BREAK FCO';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Diamond Gift
    // Ref: set-noble-victories/eelektrik.ts (Dynamotor - attach energy from discard)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;

      const hasEnergyInDiscard = player.discard.cards.some(c =>
        c instanceof EnergyCard
      );

      if (!hasEnergyInDiscard) {
        return state;
      }

      // Check if player has any Fighting Pokemon in play and build blocked list
      let hasFightingPokemon = false;
      const blocked: CardTarget[] = [];

      // Check active
      const activePokemon = player.active.getPokemonCard();
      if (activePokemon && activePokemon.cardType === CardType.FIGHTING) {
        hasFightingPokemon = true;
      } else {
        blocked.push({ player: PlayerType.BOTTOM_PLAYER, slot: SlotType.ACTIVE, index: 0 });
      }

      // Check bench
      player.bench.forEach((benchSlot, index) => {
        if (benchSlot.cards.length === 0) {
          blocked.push({ player: PlayerType.BOTTOM_PLAYER, slot: SlotType.BENCH, index });
          return;
        }
        const pokemonCard = benchSlot.getPokemonCard();
        if (pokemonCard && pokemonCard.cardType === CardType.FIGHTING) {
          hasFightingPokemon = true;
        } else {
          blocked.push({ player: PlayerType.BOTTOM_PLAYER, slot: SlotType.BENCH, index });
        }
      });

      if (!hasFightingPokemon) {
        return state;
      }

      const energyInDiscard = player.discard.cards.filter(c => c instanceof EnergyCard).length;
      const maxCards = Math.min(2, energyInDiscard);

      // Choose energy cards from discard pile
      return store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_ATTACH,
        player.discard,
        { superType: SuperType.ENERGY },
        { min: 0, max: maxCards, allowCancel: true }
      ), (selected: Card[] | null) => {
        const cards = selected || [];
        if (cards.length === 0) { return; }

        // Choose a Fighting Pokemon to attach to
        store.prompt(state, new ChoosePokemonPrompt(
          player.id,
          GameMessage.CHOOSE_POKEMON,
          PlayerType.BOTTOM_PLAYER,
          [SlotType.ACTIVE, SlotType.BENCH],
          { min: 1, max: 1, allowCancel: false, blocked }
        ), targets => {
          if (targets && targets.length > 0) {
            cards.forEach(card => {
              player.discard.moveCardTo(card, targets[0]);
            });
          }
        });
      });
    }

    return state;
  }
}
