import { PokemonCard } from '../../game/store/card/pokemon-card';
import { Stage, CardType, SuperType, EnergyType } from '../../game/store/card/card-types';
import { StoreLike, State, GameMessage, ChooseCardsPrompt, StateUtils } from '../../game';
import { Effect } from '../../game/store/effects/effect';
import { HealEffect } from '../../game/store/effects/game-effects';
import { WAS_ATTACK_USED, SHUFFLE_DECK, SHOW_CARDS_TO_PLAYER } from '../../game/store/prefabs/prefabs';

export class Butterfree extends PokemonCard {
  public stage: Stage = Stage.STAGE_2;
  public evolvesFrom: string = 'Metapod';
  public cardType: CardType = G;
  public hp: number = 130;
  public weakness = [{ type: L }];
  public resistance = [{ type: F, value: -20 }];
  public retreat = [C];

  public attacks = [
    {
      name: 'Quiver Dance',
      cost: [G],
      damage: 0,
      text: 'Search your deck for a basic Energy card and attach it to this Pokémon. Shuffle your deck afterward. If you attached Energy in this way, heal 40 damage from this Pokémon.'
    },
    {
      name: 'Gust',
      cost: [G, C, C],
      damage: 70,
      text: ''
    }
  ];

  public set: string = 'FLF';
  public setNumber: string = '3';
  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Butterfree';
  public fullName: string = 'Butterfree FLF';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Refs: set-next-destinies/cilan.ts (basic energy search), AGENTS.md (conditional attach heal)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);

      const basicEnergyCount = player.deck.cards.filter(
        c => c.superType === SuperType.ENERGY && c.energyType === EnergyType.BASIC
      ).length;

      if (basicEnergyCount === 0) {
        SHUFFLE_DECK(store, state, player);
        return state;
      }

      state = store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_ATTACH,
        player.deck,
        { superType: SuperType.ENERGY, energyType: EnergyType.BASIC },
        { min: 0, max: 1, allowCancel: false }
      ), selected => {
        const cards = selected || [];

        if (cards.length > 0) {
          SHOW_CARDS_TO_PLAYER(store, state, opponent, cards);
          cards.forEach(card => {
            player.deck.moveCardTo(card, player.active);
          });

          // Heal 40 damage since we attached energy
          const healEffect = new HealEffect(player, player.active, 40);
          store.reduceEffect(state, healEffect);
        }

        SHUFFLE_DECK(store, state, player);
        return state;
      });
    }

    return state;
  }
}
