  public fullName: string = 'Battle Reporter FFI';
  public text: string = 'Draw cards until you have the same number of cards in your hand as your opponent. You may play only 1 Supporter card during your turn (before your attack).';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Ref: set-great-encounters/baltoy.ts (Psychic Balance - draw until equal to opponent)
    if (effect instanceof TrainerEffect && effect.trainerCard === this) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);
      const supporterTurn = player.supporterTurn;

      if (supporterTurn > 0) {
        throw new GameError(GameMessage.SUPPORTER_ALREADY_PLAYED);
      }

      // Count opponent's hand size
      const opponentHandSize = opponent.hand.cards.length;
      // Current hand size (this card is already moved out of hand by the time we count,
      // so we count current hand)
      const currentHandSize = player.hand.cards.length;

      if (currentHandSize < opponentHandSize && player.deck.cards.length > 0) {
        const cardsToDraw = opponentHandSize - currentHandSize;
        DRAW_CARDS(player, cardsToDraw);
      }
    }

    return state;
  }
