  public set: string = 'GRI';
  public setNumber: string = '61';
  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Lunala';
  public fullName: string = 'Lunala GRI';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Shatter Shot
    // Ref: set-sun-and-moon/gumshoos-gx.ts (Gumshoe Chance-GX - energy count)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;

      const checkEnergy = new CheckProvidedEnergyEffect(player, player.active);
      store.reduceEffect(state, checkEnergy);

      let psychicCount = 0;
      checkEnergy.energyMap.forEach(em => {
        psychicCount += em.provides.filter(p => p === CardType.PSYCHIC || p === CardType.ANY).length;
      });

      effect.damage = 40 * psychicCount;
    }

    // Attack 2: Wings of the Moone
    // Ref: set-noble-victories/victini-2.ts (Stored Power)
    if (WAS_ATTACK_USED(effect, 1, this)) {
      const player = effect.player;
      const hasBench = player.bench.some(b => b.cards.length > 0);

      if (!hasBench) {
        return state;
      }

      const attachedEnergies = player.active.cards.filter(card =>
        card.superType === SuperType.ENERGY
      );

      if (attachedEnergies.length === 0) {
        return state;
      }

      return store.prompt(state, new AttachEnergyPrompt(
        player.id,
        GameMessage.ATTACH_ENERGY_TO_BENCH,
        player.active,
        PlayerType.BOTTOM_PLAYER,
        [SlotType.BENCH],
        { superType: SuperType.ENERGY },
        { allowCancel: false, min: attachedEnergies.length, max: attachedEnergies.length }
      ), transfers => {
        transfers = transfers || [];
        for (const transfer of transfers) {
          const target = StateUtils.getTarget(state, player, transfer.to);
          player.active.moveCardTo(transfer.card, target);
        }
      });
    }

    return state;
  }
