import { UseStadiumEffect, MoveCardsEffect } from '../../game/store/effects/game-effects';
import { EnergyCard } from '../../game/store/card/energy-card';

export class HeatFactory extends TrainerCard {
  public trainerType: TrainerType = TrainerType.STADIUM;
  public tags = [CardTag.PRISM_STAR];
  public set: string = 'LOT';
  public setNumber: string = '178';
  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Heat Factory \u25c7';
  public fullName: string = 'Heat Factory \u25c7 LOT';
  public text: string = 'Once during each player\'s turn, that player may discard a [R] Energy card from their hand. If they do, they draw 3 cards. Whenever any player plays an Item or Supporter card from their hand, prevent all effects of that card done to this Stadium card. \u25c7 (Prism Star) Rule: You can\'t have more than 1 \u25c7 card with the same name in your deck. If a \u25c7 card would go to the discard pile, put it in the Lost Zone instead.';

  // Ref: set-lost-thunder/heat-factory-prism-star.ts (existing implementation)
  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    if (effect instanceof UseStadiumEffect && StateUtils.getStadiumCard(state) === this) {
      const player = effect.player;
      const stadiumUsedTurn = player.stadiumUsedTurn;

      let hasFireEnergy = false;
      const blocked: number[] = [];
      player.hand.cards.forEach((c, index) => {
        if (c instanceof EnergyCard) {
          if (c.provides.includes(CardType.FIRE)) {
            hasFireEnergy = true;
          } else {
            blocked.push(index);
          }
        }
      });

      if (!hasFireEnergy) {
        throw new GameError(GameMessage.CANNOT_USE_POWER);
      }

      return store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_DISCARD,
        player.hand,
        { superType: SuperType.ENERGY },
        { allowCancel: true, min: 1, max: 1, blocked }
      ), selected => {
        selected = selected || [];
        if (selected.length === 0) {
          player.stadiumUsedTurn = stadiumUsedTurn;
          return;
        }
        player.hand.moveCardsTo(selected, player.discard);
        player.deck.moveTo(player.hand, 3);
      });
    }

    // Prevent effects of Item and Supporter cards on this Stadium
    if (effect instanceof MoveCardsEffect
      && StateUtils.getStadiumCard(state) === this) {

      if (effect.sourceCard instanceof TrainerCard &&
        (effect.sourceCard.trainerType === TrainerType.SUPPORTER || effect.sourceCard.trainerType === TrainerType.ITEM)) {

        const stadiumCard = StateUtils.getStadiumCard(state);
        if (stadiumCard !== undefined) {
          const cardList = StateUtils.findCardList(state, stadiumCard);
          if (effect.source === cardList) {
            effect.preventDefault = true;
          }
        }
      }
    }

    return state;
  }
}
