// Ref: set-phantom-forces/target-whistle.ts (Target Whistle), set-chilling-reign/echoing-horn.ts (Echoing Horn)
function* playCard(next: Function, store: StoreLike, state: State, effect: TrainerEffect): IterableIterator<State> {
  const player = effect.player;
  const opponent = StateUtils.getOpponent(state, player);
  const slots: PokemonCardList[] = opponent.bench.filter(b => b.cards.length === 0);

  // Check if opponent's discard has Basic Pokemon
  const hasBasicInDiscard = opponent.discard.cards.some(c =>
    c.superType === SuperType.POKEMON && (c as any).stage === Stage.BASIC
  );

  if (!hasBasicInDiscard) {
    throw new GameError(GameMessage.CANNOT_PLAY_THIS_CARD);
  }

  // Check if bench has open slots
  if (slots.length === 0) {
    throw new GameError(GameMessage.CANNOT_PLAY_THIS_CARD);
  }

  effect.preventDefault = true;

  let cards: Card[] = [];
  yield store.prompt(state, new ChooseCardsPrompt(
    player,
    GameMessage.CHOOSE_CARD_TO_HAND,
    opponent.discard,
    { superType: SuperType.POKEMON, stage: Stage.BASIC },
    { min: 1, max: 1, allowCancel: false }
  ), selected => {
    cards = selected || [];
    next();
  });

  if (cards.length > 0) {
    opponent.discard.moveCardTo(cards[0], slots[0]);
    slots[0].pokemonPlayedTurn = state.turn;
  }

  player.supporter.moveCardTo(effect.trainerCard, player.discard);
}

