  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Electribonus
    // Ref: set-surging-sparks/klefki.ts (Plug and Draw - discard from hand then draw)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;

      const lightningEnergyInHand = player.hand.cards.filter(card =>
        card instanceof EnergyCard && card.provides.includes(CardType.LIGHTNING)
      );

      if (lightningEnergyInHand.length === 0) {
        return state;
      }

      const blocked: number[] = [];
      player.hand.cards.forEach((c, index) => {
        if (!(c instanceof EnergyCard) || !c.provides.includes(CardType.LIGHTNING)) {
          blocked.push(index);
        }
      });

      state = store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_DISCARD,
        player.hand,
        { superType: SuperType.ENERGY, energyType: EnergyType.BASIC },
        { min: 1, max: 1, allowCancel: false, blocked }
      ), cards => {
        cards = cards || [];
        if (cards.length > 0) {
          player.hand.moveCardsTo(cards, player.discard);
          DRAW_CARDS(player, 3);
        }
      });
    }

    // Attack 2: Poltergeist
    // Ref: set-team-up/gengar-and-mimikyu-gx.ts (Poltergeist)
    if (WAS_ATTACK_USED(effect, 1, this)) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);

      SHOW_CARDS_TO_PLAYER(store, state, player, opponent.hand.cards);

      const trainerCount = opponent.hand.cards.filter(card => card instanceof TrainerCard).length;
      effect.damage = 20 * trainerCount;
    }

    return state;
  }
