import { Effect } from '../../game/store/effects/effect';
import { WAS_ATTACK_USED, COIN_FLIP_PROMPT } from '../../game/store/prefabs/prefabs';

export class Magikarp extends PokemonCard {
  public stage: Stage = Stage.BASIC;
  public cardType: CardType = W;
  public hp: number = 30;
  public weakness = [{ type: L }];
  public retreat = [C];

  public attacks = [
    {
      name: 'Enter the Dragon',
      cost: [W],
      damage: 0,
      text: 'Flip a coin. If heads, put a card that evolves from this Pokémon from your discard pile onto this Pokémon to evolve it.'
    }
  ];

  public set: string = 'TEU';
  public setNumber: string = '29';
  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Magikarp';
  public fullName: string = 'Magikarp TEU';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Enter the Dragon
    // Ref: AGENTS.md (manual evolution from discard pattern)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;

      // Check if there is a card that evolves from this Pokemon in the discard
      const evolvesCards = player.discard.cards.filter(c =>
        c instanceof PokemonCard && c.evolvesFrom === this.name
      );

      if (evolvesCards.length === 0) {
        return state;
      }

      COIN_FLIP_PROMPT(store, state, player, result => {
        if (!result) {
          return;
        }

        // Block non-matching cards
        const blocked: number[] = [];
        player.discard.cards.forEach((c, index) => {
          if (c instanceof PokemonCard && c.evolvesFrom === this.name) {
            return;
          }
          blocked.push(index);
        });

        store.prompt(state, new ChooseCardsPrompt(
          player,
          GameMessage.CHOOSE_CARD_TO_EVOLVE,
          player.discard,
          { superType: SuperType.POKEMON },
          { min: 1, max: 1, allowCancel: false, blocked }
        ), (selected: Card[] | null) => {
          const cards = selected || [];
          if (cards.length > 0) {
            const evolutionCard = cards[0] as PokemonCard;
            const pokemonCardList = StateUtils.findCardList(state, this) as PokemonCardList;
            player.discard.moveCardTo(evolutionCard, pokemonCardList);
            pokemonCardList.clearEffects();
            pokemonCardList.pokemonPlayedTurn = state.turn;
          }
        });
      });
    }

    return state;
  }
}
