function* useExeggutorsParadise(
  next: Function,
  store: StoreLike,
  state: State,
  player: Player,
  benchedExeggcute: { list: PokemonCardList, card: PokemonCard }[]
): IterableIterator<State> {
  for (const exeggcute of benchedExeggcute) {
    if (player.deck.cards.length === 0) break;

    // Block cards that aren't Alolan Exeggutor or Alolan Exeggutor-GX
    const blocked: number[] = [];
    player.deck.cards.forEach((card, index) => {
      if (!(card instanceof PokemonCard && card.evolvesFrom === 'Exeggcute'
        && (card.name === 'Alolan Exeggutor' || card.name === 'Alolan Exeggutor-GX'))) {
        blocked.push(index);
      }
    });

    // Check if there are any valid evolution cards
    const hasEvolution = player.deck.cards.some(c =>
      c instanceof PokemonCard && c.evolvesFrom === 'Exeggcute'
      && (c.name === 'Alolan Exeggutor' || c.name === 'Alolan Exeggutor-GX')
    );
    if (!hasEvolution) continue;

    let cards: Card[] = [];
    yield store.prompt(state, new ChooseCardsPrompt(
      player,
      GameMessage.CHOOSE_CARD_TO_EVOLVE,
      player.deck,
      { superType: SuperType.POKEMON, evolvesFrom: 'Exeggcute' },
      { min: 0, max: 1, allowCancel: true, blocked }
    ), selected => {
      cards = selected || [];
      next();
    });

    if (cards.length > 0) {
      const evolution = cards[0] as PokemonCard;
      player.deck.moveCardTo(evolution, exeggcute.list);
      exeggcute.list.clearEffects();
      exeggcute.list.pokemonPlayedTurn = state.turn;
    }
  }

  return SHUFFLE_DECK(store, state, player);
}
